import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { FaArrowLeft } from 'react-icons/fa';

const EmailPage = () => {
  const { id } = useParams();
  const [email, setEmail] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchEmail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/emails/${id}`);
        setEmail(response.data);
      } catch (err) {
        setError('Failed to fetch email');
      }
    };

    fetchEmail();
  }, [id]);

  if (error) return <div className="text-red-500 p-4">{error}</div>;
  if (!email) return <div className="text-gray-500 p-4">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header Section */}
      <header className="bg-white shadow-md py-4 px-6 flex items-center">
        <button onClick={() => window.history.back()} className="text-blue-500 hover:text-blue-600 flex items-center">
          <FaArrowLeft className="mr-2" /> Back
        </button>
        <h1 className="text-xl font-semibold ml-4">Email Details</h1>
      </header>

      {/* Email Details Section */}
      <div className="max-w-4xl mx-auto mt-8 p-6 bg-white shadow-md rounded-xl">
        <h2 className="text-2xl font-bold mb-2">{email.subject}</h2>
        <p className="text-gray-500 mb-4">From: {email.from}</p>
        <p className="text-gray-400 text-sm mb-6">Date: {new Date(email.date).toLocaleString()}</p>
        <hr className="mb-6" />
        <div className="prose" dangerouslySetInnerHTML={{ __html: email.body }} />
      </div>

      <footer className="bg--900 text-white py-6 mt-12 border-t border-blue-700">
        <div className="container mx-auto text-center">
          <p className="text-sm">© {new Date().getFullYear()} <span className="font-semibold">Amritendu</span> (2110994839). All Rights Reserved.</p>
          <p className="text-xs mt-2 opacity-80">Built with ❤️ using React and Node.js</p>
        </div>
      </footer>
    </div>
  );
};

export default EmailPage;
