import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css'; // Importing the CSS file for styling

const Dashboard = () => {
  const [emails, setEmails] = useState([]);
  const [filteredEmails, setFilteredEmails] = useState([]);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  const navigate = useNavigate();

  // Fetch emails on component mount
  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/emails');
        setEmails(response.data);
        setFilteredEmails(response.data);  // Initially display all emails
      } catch (err) {
        setError('Failed to fetch emails');
      }
    };

    fetchEmails();
  }, []);

  // Filter emails based on search query
  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
    if (event.target.value === '') {
      setFilteredEmails(emails);  // Show all emails when search is cleared
    } else {
      const filtered = emails.filter((email) =>
        email.subject.toLowerCase().includes(event.target.value.toLowerCase()) ||
        email.from.toLowerCase().includes(event.target.value.toLowerCase())
      );
      setFilteredEmails(filtered);  // Update filtered emails based on search
    }
  };

  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="min-vh-100 bg-light">
      {/* Header Section */}
      <header className="bg-secondary bg-gradient text-white py-4 text-center">
        <h1>Welcome to Onebox Gmail Dashboard</h1>
      </header>

      {/* Search Bar Section */}
      <div className="container my-5">
        <input
          type="text"
          className="form-control mb-4"
          placeholder="Search by subject or sender..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>

      {/* Email List Section */}
      <div className="container my-5">
        <h2 className="mb-4">Your Emails</h2>
        {filteredEmails.length === 0 ? (
          <p>No emails match your search criteria.</p>
        ) : (
          <div className="email-grid">
            {filteredEmails.map((email) => (
              <div
                key={email._id}
                className="email-item"
                onClick={() => navigate(`/emails/${email._id}`)}
              >
                <h5>{email.subject}</h5>
                <p className="mb-1">From: {email.from}</p>
                <small>{new Date(email.date).toLocaleString()}</small>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer Section */}
      <footer className="bg-dark text-white text-center py-3 mt-5">
        <p>Â© {new Date().getFullYear()} Amritendu (2110994839). All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Dashboard;
