// src/pages/EmailPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getEmails } from './path/to/your/api'; // Import getEmails from where it's defined

const EmailPage = () => {
  const { id } = useParams();
  const [email, setEmail] = useState(null);

  useEffect(() => {
    const fetchEmail = async () => {
      const emails = await getEmails();
      const foundEmail = emails.find(email => email.id === id); // Find email by ID
      setEmail(foundEmail);
    };
    fetchEmail();
  }, [id]);

  return (
    <div>
      {email ? (
        <div>
          <h2>{email.subject}</h2>
          <p>{email.body}</p>
        </div>
      ) : (
        <p>Loading email...</p>
      )}
    </div>
  );
};

export default EmailPage;  // Ensure you're using a default export here
