import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [emails, setEmails] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/emails');
        setEmails(response.data);
      } catch (err) {
        setError('Failed to fetch emails');
      }
    };

    fetchEmails();
  }, []);

  const handleEmailClick = (id) => {
    navigate(`/emails/${id}`);
  };

  if (error) return <div className="text-red-500 p-4">{error}</div>;
  if (!emails.length) return <div className="text-gray-500 p-4">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-blue-600 text-white py-6 px-8 shadow-md flex justify-center items-center">
        <h1 className="text-2xl font-bold">Onebox Gmail Dashboard</h1>
      </header>

      <div className="grid grid-cols-4 gap-6 p-8">
        {emails.map((email) => (
          <div
            key={email._id}
            onClick={() => handleEmailClick(email._id)}
            className="cursor-pointer p-6 bg-white shadow-md rounded-xl transform transition-transform hover:scale-105 hover:shadow-lg"
          >
            <h3 className="text-lg font-bold">{email.subject}</h3>
            <p className="text-gray-500">From: {email.from}</p>
            <p className="text-gray-400 text-sm">{new Date(email.date).toLocaleString()}</p>
          </div>
        ))}
      </div>

      <footer className="bg-blue-900 text-white py-6 mt-12 border-t border-blue-700">
        <div className="container mx-auto text-center">
          <p className="text-sm">Â© {new Date().getFullYear()} Amritendu (2110994839). All Rights Reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Dashboard;
